mapper.insert:

	select ssts_bitacoras_internas.nextval from dual

insert into sstt_bitacoras_internas (
	id_bitacora_interna
	,id_bitacora
	,id_estado
	,d_fecha_inicio
	,d_fecha_termino
	,id_ubicacion_interna
	,id_despacho
	,id_recepcion
	,id_ot
	,s_clasificacion
    )
values(
	#{id}
	,#{bitacora}
	,#{estado}
	,SYSDATE
	,#{fechaTermino}
	,#{ubicacionInterna}
	,#{despachoInterno}
	,#{recepcion}
	,#{ordenTrabajo}
	,#{clasificacion}
	)
