mapper.select:
select cpp.d_fecha_creacion, cpp.i_numero_certificado, cpp.id_cambios_aut_por_proveedor, cpp.id_producto, cpp.id_usuario, cpp.s_entregado, cpp.s_estado, cpp.s_nmro_serie, cpp.s_ot_en_proceso from sstt_cambios_aut_por_proveedor cpp where cpp.i_numero_certificado = #{numeroCertificado} and cpp.id_producto = #{producto.id}
and cpp.s_nmro_serie is null
and cpp.s_nmro_serie = #{numeroSerie}
and cpp.s_estado = 'S'

mapper.insert:

	select SSTS_CAMBIOS_AUT_POR_PROVEEDOR.nextval from dual

insert into sstt_cambios_aut_por_proveedor(
	id_cambios_aut_por_proveedor,
	i_numero_certificado, 
	s_nmro_serie,
	id_producto, 
	s_estado, 
	s_entregado,
	d_fecha_Creacion, 
	id_usuario,
	s_ot_en_proceso
) values (                         
	 #{id}
	,#{numeroCertificado}
	,#{numeroSerie}
	,#{producto.id}
	,#{vigente}
	,#{entregaCliente}
	,sysdate
	,#{usuario.id}
	,#{enProceso}
)
