mapper.select:
select count(*)
	from sstt_secciones_columnas sc


		and sc.id_usuario = #{usuario.rut, typeHandler=RunTypeHandler}


		and sc.id_seccion = #{idSeccion}


		and sc.id_rol = #{idRol}			


		and sc.id_indicador = #{idIndicador}			


		and exists (select 1 from sstt_columnas c where c.id_columna = sc.id_columna and c.s_tipo = #{tipo})

select c.id_columna ,c.s_nombre ,c.s_descripcion ,c.s_alinear ,c.i_ancho ,sc.i_orden ,c.s_propiedad ,c.s_columna ,c.s_formato from sstt_columnas c left join sstt_secciones_columnas sc on c.id_columna = sc.id_columna
and sc.id_usuario = #{usuario.rut, typeHandler=RunTypeHandler}
and sc.id_seccion = #{idSeccion}
and sc.id_rol = #{idRol}
and sc.id_indicador = #{idIndicador}
and c.s_tipo = #{tipo}
order by sc.i_orden, c.s_descripcion asc
