mapper.select:
select count(t.id_ejecutiva)
  from sstt_ejecutivas_sta_marcas t
       left outer join owv_productos p
         on p.id_marca = t.id_marca




			p.id_producto = #{idProducto}


			t.id_stecnico = #{idSTecnico}
			and p.id_producto = #{idProducto}

select 
	t.id_ejecutiva
from sstt_ejecutivas_sta_marcas t
	left outer join owv_productos p on p.id_marca = t.id_marca
where t.id_stecnico = #{idSTecnico}
	and p.id_producto = #{idProducto}

select us.id_usuario ,us.s_nombre ,us.s_apellido_paterno ,us.s_apellido_materno ,(s_nombre || ' ' || s_apellido_paterno || ' ' || s_apellido_materno) as nombre_Completo ,us.s_celular ,us.s_interno ,us.s_email ,us.id_ubicacion from sstt_usuarios us left join sstt_ejecutivas_sta_marcas em on em.id_ejecutiva = us.id_usuario left join owv_productos pr on pr.id_marca = em.id_marca
pr.id_producto = #{idProducto} and ROWNUM < 2
em.id_stecnico = #{idSTecnico} and pr.id_producto = #{idProducto}
