mapper.select:
select su.id_ubicacion ,su.s_nombre as s_nombre_sucursal ,su.s_direccion ,es.d_fecha_inicio as d_fecha_inicio ,es.d_fecha_cierre as d_fecha_cierre ,SUM(case when bi.id_ubicacion_interna =1001500003 then 1 else 0 end) as i_total_leidas ,SUM(case when bi.id_estado = 100010200 then 1 else 0 end) as i_almacenadas from sstt_ubicaciones su left join sstt_envios_sucursales es on es.id_sucursal = su.id_ubicacion left join sstt_ordenes_trabajo ot on ot.id_sucursal = su.id_ubicacion left join sstt_bitacoras_internas bi on bi.id_ot = ot.id_ot and d_fecha_termino is null where su.s_tipo = 'SC' and su.s_vigente ='S' and su.s_vigente ='S' and (es.id_envio_sucursal in (select max(es2.id_envio_sucursal) from sstt_envios_sucursales es2 where es2.id_sucursal = es.id_sucursal) or su.id_ubicacion not in (select id_sucursal from sstt_envios_sucursales) )
es.d_fecha_inicio = #{fecha_inicio}
group by su.id_ubicacion ,su.s_nombre ,su.s_direccion ,es.d_fecha_inicio ,es.d_fecha_cierre
order by ${orderBy}
${sortOrder}

select count(1)
from sstt_ubicaciones su
where su.s_tipo ='SC'
	and su.s_vigente = 'S'

	order by ${orderBy}

		${sortOrder}

mapper.insert:

	select ssts_envios_sucursales.nextval from dual

insert into sstt_envios_sucursales(
	id_envio_sucursal
	,id_sucursal
	,d_fecha_inicio
	,id_usuario_inicio
	,i_cant_ot
) values (
	#{id}
	,#{sucursal.id}
	,sysdate
	,#{usuarioInicio.id}
	,#{totalOT}
)

mapper.update:
update sstt_envios_sucursales
	set d_fecha_cierre = sysdate
		,id_usuario_cierre = #{usuarioTermino.id}
where id_envio_sucursal = #{id}
