mapper.select:
select a.id_guia, a.i_numero, a.id_destino, a.s_tipo_ubicacion, a.s_nombre, a.s_direccion, a.s_estado_guia, a.id_estado, a.id_origen, a.s_tipo_cambio, p.s_descripcion, sum(i_cant_ot) as i_cant_ot, sum(i_cant_prod) as i_cant_prod from ( select g.id_guia, g.i_numero, g.id_destino, u.s_tipo AS s_tipo_ubicacion, u.s_nombre, u.s_direccion, e.s_nombre AS s_estado_guia, e.id_estado, g.id_origen, case when ot.s_tipo_cambio = 'JT' then ot.s_tipo_cambio_jt else ot.s_tipo_cambio end as s_tipo_cambio, count(distinct ot.id_ot) as i_cant_ot, count(distinct ot.id_producto) as i_cant_prod from sstt_guias g inner join sstt_bitacoras b on b.id_guia = g.id_guia inner join sstt_ordenes_trabajo ot on ot.id_ot = b.id_ot left join sstt_ubicaciones u on u.id_ubicacion = g.id_destino left join sstt_estados e on e.id_estado = g.id_estado where g.s_vigente = 'S' and g.id_estado IN (50001000, 50001500, 50002000) and g.id_origen = #{idOrigen} and g.id_ot is null group by g.id_guia, g.i_numero, g.id_destino, u.s_tipo, u.s_nombre, u.s_direccion, e.s_nombre, e.id_estado, g.id_origen, ot.s_tipo_cambio, ot.s_tipo_cambio_jt ) a left join sstt_parametros p on p.s_nombre = a.s_tipo_cambio and p.s_string1 = 'TICA' group by a.id_guia, a.i_numero, a.id_destino, a.s_tipo_ubicacion, a.s_nombre, a.s_direccion, a.s_estado_guia, a.id_estado, a.id_origen, p.s_descripcion, a.s_tipo_cambio
order by a.id_estado asc, a.s_tipo_cambio desc
order by ${orderBy}
${sortOrder}

select g.id_guia, g.i_numero, g.id_destino, u.s_tipo AS s_tipo_ubicacion, u.s_nombre, u.s_direccion, e.s_nombre AS s_estado_guia, e.id_estado, g.id_origen, count(distinct ot.id_ot) as i_cant_ot, count(distinct ot.id_producto) as i_cant_prod from sstt_guias g inner join sstt_bitacoras b on b.id_guia = g.id_guia inner join sstt_ordenes_trabajo ot on ot.id_ot = b.id_ot left join sstt_ubicaciones u on u.id_ubicacion = g.id_destino left join sstt_estados e on e.id_estado = g.id_estado where g.s_vigente = 'S' and g.id_estado IN (50001000, 50001500) and g.id_origen = #{idUbicacion} and g.id_destino = 11 and g.id_ot is null group by g.id_guia, g.i_numero, g.id_destino, u.s_tipo, u.s_nombre, u.s_direccion, e.s_nombre, e.id_estado, g.id_origen, g.id_estado
order by g.id_estado asc
order by ${orderBy}
${sortOrder}
