mapper.insert:

	select ssts_guias_remate.nextval from dual

insert into sstt_guias_remate (
	id_guia_remate,
	id_usuario,
	id_origen,
	id_destino,
	id_estado,
	d_fecha_registro,
	s_vigente
) values (
	#{id}
	,#{usuario.id}
	,#{origen.id}
	,#{destino.id}
	,#{estado.id}
	,sysdate
	,#{vigente}
)


	select ssts_guias_remate_detalle.nextval from dual

insert into sstt_guias_remate_detalle (
	id_guia_remate_detalle,
	id_guia_remate,
	id_ot
) values (
	#{id}
	,#{guia.id}
	,#{ordenTrabajo.id}
)

mapper.select:
select g.id_guia_remate ,g.i_numero ,count(gd.id_guia_remate_detalle) as i_cantidad_ots ,d.s_tipo ,d.s_nombre as s_destino ,d.s_direccion ,g.id_estado as id_estado_guia ,e.s_nombre as s_estado_guia from sstt_guias_remate g inner join sstt_guias_remate_detalle gd on gd.id_guia_remate = g.id_guia_remate left join sstt_ubicaciones d on d.id_ubicacion = g.id_destino inner join sstt_estados e on e.id_estado = g.id_estado where g.s_vigente = 'S' and g.id_estado in (50001000,50002000) group by g.id_guia_remate ,g.i_numero ,d.s_tipo ,d.s_nombre ,d.s_direccion ,g.id_estado ,e.s_nombre
order by ${orderBy}
${sortOrder}

mapper.update:
update sstt_guias_remate g
   set g.id_estado = #{estado.id}
 where g.id_guia_remate = #{id}

		UPDATE sstt_guias_remate g
           SET g.id_usuario        = #{usuario.id}
              ,g.id_estado         = #{estado.id}
              ,g.id_transportista  = #{transportista.id}
              ,g.i_numero          = #{numero}
              ,g.d_fecha_emision   = #{fechaEmision}
              ,g.s_patente         = #{transportista.patente}
              ,g.d_fecha_impresion = sysdate
         WHERE 
         	g.id_guia_remate = #{id}
