mapper.select:
select count(*)
	from owt_facturas f
where 
	f.id_estado = 40001000
	and	f.d_fecha_registro >= #{fechaInicio}
	and f.d_fecha_registro <= #{fechaTermino}

select f.s_nombre ,f.d_fecha_emision ,f.i_numero ,e.s_nombre as estado from owt_facturas f inner join sstt_estados e on e.id_estado = f.id_estado where f.id_estado = 40001000 and f.d_fecha_registro >= #{fechaInicio} and f.d_fecha_registro <= #{fechaTermino}
order by ${orderBy}
${sortOrder}

select count(*)
	from owt_facturas f
where 
	f.id_estado = 40003000
	and	f.d_fecha_emision >= #{fechaInicio}
	and f.d_fecha_emision <= #{fechaTermino}

select f.s_nombre ,f.d_fecha_emision ,f.i_numero ,e.s_nombre as estado from owt_facturas f inner join sstt_estados e on e.id_estado = f.id_estado where f.id_estado = 40003000 and f.d_fecha_emision >= #{fechaInicio} and f.d_fecha_emision <= #{fechaTermino}
order by ${orderBy}
${sortOrder}

select count(*)
	from owt_facturas f
where 
	f.id_estado = 40004000
	and	f.d_fecha_aceptacion >= #{fechaInicio}
	and f.d_fecha_aceptacion <= #{fechaTermino}

select f.s_nombre ,f.d_fecha_emision ,f.i_numero ,e.s_nombre as estado from owt_facturas f inner join sstt_estados e on e.id_estado = f.id_estado where f.id_estado = 40004000 and f.d_fecha_aceptacion >= #{fechaInicio} and f.d_fecha_aceptacion <= #{fechaTermino}
order by ${orderBy}
${sortOrder}

select count(*)
	from owt_facturas f
where 
	f.id_estado = 40005000
	and	f.d_fecha_aceptacion >= #{fechaInicio}
	and f.d_fecha_aceptacion <= #{fechaTermino}

select f.s_nombre ,f.d_fecha_emision ,f.i_numero ,e.s_nombre as estado from owt_facturas f inner join sstt_estados e on e.id_estado = f.id_estado where f.id_estado = 40005000 and f.d_fecha_aceptacion >= #{fechaInicio} and f.d_fecha_aceptacion <= #{fechaTermino}
order by ${orderBy}
${sortOrder}

	select
	    f.s_nombre
	    ,f.d_fecha_emision
	    ,f.i_numero  
	    ,e.s_nombre as estado
	from owt_facturas f	               
	    INNER JOIN sstt_facturas_ot fot ON fot.id_factura = f.id_factura
	    INNER JOIN sstt_ordenes_trabajo ot ON ot.id_ot = fot.id_ot
	where f.id_estado = 40005000
		AND ot.s_vigente = 'S'
		AND f.i_rut != 89772300
		AND f.i_rut != 82982300

               and f.i_rut = #{rut}


               and ot.s_tipo = #{tipoOT}


              and ot.id_ejecutiva = #{idEjecutiva}


              and f.d_fecha_emision >= #{fechaInicio}


              and f.d_fecha_emision <=  #{fechaTermino} + 1


              and ot.s_facturar_tipo =  #{idFacturar}


		order by ${orderBy}

			${sortOrder}

		select
  			f.s_nombre as s_nombre
  			,f.i_numero as i_numero
  			,f.d_fecha_emision as d_fecha_emision
  			,e.s_descripcion as estado
		from owt_facturas f
  			left join sstt_estados e on e.id_estado = f.id_estado
  		where f.i_rut = #{rut}

			order by ${orderBy}

				${sortOrder}
