select count(distinct ot.id_ot) from sstt_ordenes_trabajo ot inner join sstt_bitacoras bitacoras on ot.id_ot = bitacoras.id_ot where ot.s_vigente = 'S' and ot.s_tipo in ('GP','GPC') and ot.s_cambio_autorizado = 'N' and ot.s_cerrada_cliente = 'N' and ot.s_cerrada = 'N' and bitacoras.d_fecha_entrada is not null and bitacoras.d_fecha_salida is null and bitacoras.id_ubicacion = ot.id_sucursal and ot.id_estado = 10003000
and ot.i_semaforo_sucursal_inicio >= #{idSemaforo}
and ot.i_semaforo_sucursal_inicio = #{idSemaforo}

