mapper.select:
select mp.id_pestana id_modulo, m.s_codigo, m.s_nombre, m.s_macro, m.s_tipo, m.s_etiqueta, m.s_icono, m.i_modulo_padre, m.s_migrado, avg(mp.i_orden) i_orden from sstt_usuarios_roles ur inner join sstt_roles r on r.id_rol = ur.id_rol inner join sstt_modulos_pestanas mp on mp.id_rol = r.id_rol inner join sstt_modulos m on m.id_modulo = mp.id_pestana
r.s_vigente = 'S'
and ur.id_usuario = #{rut, typeHandler=RunTypeHandler}
and mp.id_modulo = #{idModulo}
group by mp.id_pestana, m.s_codigo, m.s_nombre, m.s_macro, m.s_tipo, m.s_etiqueta, m.s_icono, m.i_modulo_padre, m.s_migrado order by i_orden asc

select m.id_modulo, m.s_codigo, m.s_nombre, m.s_macro, m.s_tipo, m.s_etiqueta, m.i_orden, m.s_icono, m.i_modulo_padre, m.s_migrado, m.s_descripcion from sstt_modulos m where m.s_vigente = 'S' and m.s_tipo in ('M','E','P') and exists (select 1 from sstt_roles_modulos rm where m.id_modulo = rm.id_modulo and rm.id_rol = #{idRol} )
and m.s_macro = #{macro}
order by m.s_macro asc, m.s_tipo asc, m.s_etiqueta asc

select m.id_modulo, m.s_codigo, m.s_nombre, m.s_macro, m.s_tipo, m.s_etiqueta, m.i_orden, m.s_icono, m.i_modulo_padre, m.s_migrado, m.s_descripcion from sstt_modulos m where m.s_vigente = 'S' and m.s_tipo in ('M','E','P') and not exists (select 1 from sstt_roles_modulos rm where m.id_modulo = rm.id_modulo and rm.id_rol = #{idRol} )
and m.s_macro = #{macro}
order by m.s_macro asc, m.s_tipo asc, m.s_etiqueta asc

mapper.delete:
delete from sstt_modulos_pestanas
where 
	id_rol = #{idRol}
and id_modulo = #{idModulo}
