mapper.select:
select ot.id_ot, ot.i_cod_barra, ot.id_producto, ot.id_documento, ot.s_tipo_documento, ot.s_nmro_serie, ot.id_sucursal, u.s_nombre as s_nombre_sucursal, ot.id_estado as id_estado_ot, eot.s_descripcion as s_estado_ot, ot.d_fecha_creacion as d_fecha_creacion, r.id_estado as id_estado_rec_ot, ere.s_descripcion as s_estado_recepcion, rco.d_fecha_recepcion as d_fecha_recepcion, g.i_numero as i_numero_guia, g.id_estado as id_estado_guia, eg.s_descripcion as s_estado_guia, g.id_estado_actual as id_estado_actual_guia, eag.s_descripcion as s_estado_actual_guia, tr.id_transportista, tr.s_nombre s_nombre_transportista, rc.id_recepcion_camion, usu_rec.id_usuario as id_usuario_recepcion, (usu_rec.s_nombre || ' ' || usu_rec.s_apellido_paterno || ' ' || usu_rec.s_apellido_materno) as s_nombre_usu_recepcion, rc.s_responsable as s_nombre_responsable, rc.d_fecha_inicio, z.id_zona, z.s_nombre as s_nombre_zona, l.id_linea, l.s_nombre as s_nombre_linea, f.id_familia, f.s_nombre as s_nombre_familia, p.id_producto, p.s_descripcion as s_nombre_producto, m.id_marca, m.s_nombre as s_nombre_marca from sstt_recepciones_camion rc inner join sstt_recepciones_camion_guias rcg on rcg.id_recepcion_camion = rc.id_recepcion_camion inner join sstt_recepciones_camion_ot rco on rco.id_recepcion_camion_guia = rcg.id_recepcion_camion_guia inner join sstt_guias g on g.id_guia = rcg.id_guia inner join sstt_estados eg on eg.id_estado = rcg.id_estado_guia inner join sstt_estados eag on eag.id_estado = g.id_estado inner join sstt_ordenes_trabajo ot on ot.id_ot = rco.id_ot inner join sstt_estados eot on eot.id_estado = rco.id_estado_ot left join sstt_recepciones r on r.id_ot = ot.id_ot and r.i_nmro_guia = g.i_numero left join sstt_estados ere on ere.id_estado = r.id_estado inner join owv_transportistas tr on tr.id_transportista = rc.id_transportista inner join sstt_usuarios usu_rec on usu_rec.id_usuario = rc.id_logistico inner join sstt_ubicaciones u on u.id_ubicacion = ot.id_sucursal left join sstt_ubicaciones_zonas uz on uz.id_ubicacion = u.id_ubicacion left join sstt_zonas z on z.id_zona = uz.id_zona left join owv_productos p on p.id_producto = ot.id_producto left join owv_familias f on f.id_familia = p.id_familia left join owv_lineas l on l.id_linea = f.id_linea left join owv_marcas m on m.id_marca = p.id_marca
and tr.id_transportista = #{transportista}
and rc.id_recepcion_camion = #{idRecepcion}
and usu_rec.id_usuario = #{usuarioRecepcion.id}
and rc.s_responsable = upper(#{responsable})
and g.i_numero = #{numeroGuia}
and rco.id_estado_guia = #{estadoRecepcionGuia.id}
and g.id_estado = #{estadoActualGuia.id}
and rco.id_estado_ot = #{estadoRecepcionOT.id}
and z.id_zona = #{idZona}
and ot.id_sucursal = #{idSucursal}
and ot.id_ot = #{idOT}
and l.id_linea = #{idLinea}
and f.id_familia = #{idFamilia}
and p.id_producto = #{idProducto}
and m.id_marca = #{idMarca}
and ot.s_nmro_serie = #{numeroSerie}
and ot.i_cod_barra = #{codigoBarras, typeHandler=CodigoBarrasTypeHandler}
and r.d_fecha_recepcion >= #{fechaRecepcionInicio}
and r.d_fecha_recepcion <= #{fechaRecepcionFin} + 1
and ot.d_fecha_creacion >= #{fechaCreacion}
and ot.d_fecha_creacion <= #{fechaCreacionFin} + 1
order by ${orderBy}
${sortOrder}

		select
			count(*)
		from
		    sstt_recepciones_camion rc
		    inner join sstt_recepciones_camion_guias rcg on rcg.id_recepcion_camion = rc.id_recepcion_camion
		    inner join sstt_recepciones_camion_ot rco on rco.id_recepcion_camion_guia = rcg.id_recepcion_camion_guia
		    inner join sstt_guias g on g.id_guia = rcg.id_guia 
		    inner join sstt_estados eg on eg.id_estado = rcg.id_estado_guia
		    inner join sstt_estados eag on eag.id_estado = g.id_estado
		    inner join sstt_ordenes_trabajo ot on ot.id_ot = rco.id_ot
		    inner join sstt_estados eot on eot.id_estado = rco.id_estado_ot
		    left join sstt_recepciones r on r.id_ot = ot.id_ot and r.i_nmro_guia = g.i_numero
		    left join sstt_estados ere on ere.id_estado = r.id_estado
		    inner join owv_transportistas tr on tr.id_transportista = rc.id_transportista
		    inner join sstt_usuarios usu_rec on usu_rec.id_usuario = rc.id_logistico
		    inner join sstt_ubicaciones u on u.id_ubicacion = ot.id_sucursal
		    left join sstt_ubicaciones_zonas uz on uz.id_ubicacion = u.id_ubicacion
		    left join sstt_zonas z on z.id_zona = uz.id_zona
		    left join owv_productos p on p.id_producto = ot.id_producto
		    left join owv_familias f on f.id_familia = p.id_familia
		    left join owv_lineas l on l.id_linea = f.id_linea
		    left join owv_marcas m on m.id_marca = p.id_marca


 				and  tr.id_transportista = #{transportista} 


 				and rc.id_recepcion_camion = #{idRecepcion} 


 				and usu_rec.id_usuario = #{usuarioRecepcion.id} 


 				and rc.s_responsable = upper(#{responsable}) 


 				and g.i_numero = #{numeroGuia} 


 				and rco.id_estado_guia = #{estadoRecepcionGuia.id} 


 				and g.id_estado = #{estadoActualGuia.id}


 				and rco.id_estado_ot = #{estadoRecepcionOT.id} 


 				and z.id_zona = #{idZona} 


 				and ot.id_sucursal = #{idSucursal} 


 				and ot.id_ot = #{idOT} 


 				and l.id_linea = #{idLinea} 


 				and f.id_familia = #{idFamilia} 


 				and p.id_producto = #{idProducto}


 				and m.id_marca = #{idMarca} 


 				and ot.s_nmro_serie = #{numeroSerie} 


 				and ot.i_cod_barra = #{codigoBarras, typeHandler=CodigoBarrasTypeHandler} 


 				and r.d_fecha_recepcion >= #{fechaRecepcionInicio} 


 				and r.d_fecha_recepcion <= #{fechaRecepcionFin} + 1 


 				and ot.d_fecha_creacion >= #{fechaCreacion} 


 				and ot.d_fecha_creacion <= #{fechaCreacionFin} + 1
