mapper.insert:

	select ssts_peticion_documentos.nextval from dual

insert into sstt_peticion_documentos(
	id_peticion_documento
	,i_numero
	,s_tipo
	,id_facturar
	,s_vigente
) values (
	#{id}
	,#{numero}
	,#{tipo}
	,#{facturar}
	,#{vigente}
)

mapper.update:
update sstt_peticion_documentos set 
	s_vigente = #{vigente}
	,i_numero = #{numero}
where id_peticion_documento = #{id}
