mapper.update:
update sstt_recepciones_camion_ot set
id_estado_ot = #{ordenTrabajo.estado.id}
,d_fecha_recepcion = sysdate
where id_recepcion_camion_ot = #{id}

mapper.insert:

	select ssts_recepciones_camion.nextval from dual

insert into sstt_recepciones_camion (
	id_recepcion_camion
	,id_transportista
	,s_responsable
	,id_estado
	,id_logistico
	,d_fecha_inicio
	,d_fecha_termino
	,s_observacion
	)
values(
	#{id}
	,#{transportista.id}
	,#{nombreResponsable}
	,#{estado.id}
	,#{logistico.rut, typeHandler=RunTypeHandler}
	,sysdate
	,#{fechaTermino}
	,#{observacion}
	)

mapper.select:
select g.id_guia ,g.id_estado ,g.id_ot ,g.id_origen ,g.id_destino ,g.id_usuario ,g.id_transportista ,g.i_numero ,g.d_fecha_emision ,g.d_fecha_registro ,g.s_patente ,g.s_vigente ,g.s_observacion ,g.s_entrega_cliente ,g.id_estado_actual ,g.d_fecha_ultimo_estado from sstt_guias g left join sstt_bitacoras b on b.id_guia = g.id_guia left join sstt_ordenes_trabajo ot on ot.id_ot = b.id_ot where g.s_vigente = 'S' and g.id_estado = #{idEstadoGuia}
and ot.i_cod_barra = #{codigoBarra, typeHandler=CodigoBarrasTypeHandler}
and ot.id_ot = #{idOT}
and ot.s_nmro_serie = #{numeroSerie}
and (g.id_origen = #{idDestino} or g.id_destino = #{idDestino} )

select count(*)
from sstt_recepciones_camion_guias
where id_recepcion_camion = #{idRecepcionCamion}
	and id_guia = #{idGuia}

 	select
	    count(*)
	from 
	    sstt_recepciones_camion_guias rcg
	    inner join sstt_recepciones_camion rc on rc.id_recepcion_camion = rcg.id_recepcion_camion
	where
	    	id_guia = #{idGuia}
	    and rc.id_estado = #{idEstadoRecepcion}
	    and rcg.id_recepcion_camion != #{idRecepcionCamion}

mapper.insert:

			select ssts_recepciones_camion_guias.nextval from dual

	 	insert 
	 	into sstt_recepciones_camion_guias (
	 		id_recepcion_camion_guia
	 		,id_recepcion_camion
	 		,id_guia
	 		,d_fecha_inicio
	 	) values (
	 		#{id}
	 		,#{recepcionCamion.id}
	 		,#{guia.id}
	 		,sysdate
 		)

mapper.update:
update sstt_recepciones_camion_guias
set 
	d_fecha_termino = sysdate
	,id_estado_guia = #{estadoGuia.id}
where
	id_recepcion_camion_guia = #{id}

mapper.select:
select g.id_guia, g.i_numero, g.id_origen, o.s_nombre, g.d_fecha_emision, g.i_nmro_to, count(ot.id_ot) i_total_ot, sum(case when ot.id_estado_ot in (10031000, 10032000, 10033000, 10034000, 10035000, 10039000) then 1 else 0 end) i_ot_recibidas, sum(case when ot.id_estado_ot in (10032000, 10033000, 10034000, 10035000, 10039000) then 1 else 0 end) i_ot_revisadas, sum(case when ot.id_estado_ot in (10031000, 10032000, 10033000, 10034000, 10035000, 10039000) then 1 else 0 end) - sum(case when ot.id_estado_ot in (10032000, 10033000, 10034000, 10035000, 10039000) then 1 else 0 end) as faltantes from sstt_recepciones_camion rc inner join sstt_recepciones_camion_guias rcg on rcg.id_recepcion_camion = rc.id_recepcion_camion inner join sstt_recepciones_camion_ot ot on rcg.id_recepcion_camion_guia = ot.id_recepcion_camion_guia inner join sstt_guias g on g.id_guia = rcg.id_guia inner join sstt_ubicaciones o on o.id_ubicacion = g.id_origen where g.s_vigente = 'S' and rc.id_recepcion_camion = #{idRecepcionCamion} group by g.id_guia, g.i_numero, g.id_origen, o.s_nombre, g.d_fecha_emision, g.i_nmro_to
order by ${orderBy}
${sortOrder}

mapper.update:
update sstt_recepciones_camion
	set id_estado = #{estado.id}
		,d_fecha_termino = sysdate
		,s_observacion = #{observacion}
where id_recepcion_camion = #{id}
