mapper.select:
select stp.id_stecnico_producto, ub.s_nombre, ub.i_rut, ub.s_direccion, r.s_nombre as s_region, c.s_nombre as s_comuna, ub.i_cantidad_productos, ub.s_vigente, par.s_descripcion as s_garantia from sstt_stecnicos_productos stp inner join sstt_ubicaciones ub on ub.id_ubicacion = stp.id_stecnico inner join sstt_comunas c on c.id_comuna = ub.id_comuna inner join sstt_provincias p on p.id_provincia = c.id_provincia inner join sstt_regiones r on r.id_region = p.id_region inner join sstt_parametros par on par.s_nombre = stp.s_garantia and s_string1 = 'TIOT' where ub.s_tipo = 'ST' and stp.s_vigente = 'S' and stp.id_producto = #{idProducto}
order by stp.s_vigente desc, r.i_orden asc, c.s_nombre asc, ub.s_nombre asc
order by ${orderBy}
${sortOrder}

select ub.id_ubicacion ,ub.s_nombre ,ub.s_telefono ,ub.s_direccion ,c.id_comuna ,c.s_nombre as s_nombre_comuna ,r.id_region ,r.s_nombre as s_nombre_region ,to_char(ub.i_rut) as s_rut ,ub.s_giro ,ub.s_vigente ,r.i_orden ,ub.i_cantidad_productos from sstt_ubicaciones ub inner join sstt_comunas c on c.id_comuna = ub.id_comuna inner join sstt_provincias p on p.id_provincia = c.id_provincia inner join sstt_regiones r on r.id_region = p.id_region where ub.s_tipo = 'ST'
and ub.i_rut = #{rut,typeHandler=RunTypeHandler}
and upper(ub.s_nombre) like '%'||upper(#{nombre})||'%'
and ub.id_comuna = #{idComuna}
and p.id_region = #{idRegion}
and exists (select 1 from sstt_stecnicos_productos stp where stp.id_stecnico = ub.id_ubicacion and stp.id_producto = #{idProducto} )
order by ub.s_vigente desc ,ub.s_nombre asc ,r.i_orden asc ,c.id_comuna asc
order by ${orderBy}
${sortOrder}

select count(1) from sstt_ubicaciones ub inner join sstt_comunas c on c.id_comuna = ub.id_comuna inner join sstt_provincias p on p.id_provincia = c.id_provincia inner join sstt_regiones r on r.id_region = p.id_region where ub.s_tipo = 'ST'
and ub.i_rut = #{rut,typeHandler=RunTypeHandler}
and upper(ub.s_nombre) like '%'||upper(#{nombre})||'%'
and ub.id_comuna = #{idComuna}
and p.id_region = #{idRegion}
and exists (select 1 from sstt_stecnicos_productos stp where stp.id_stecnico = ub.id_ubicacion and stp.id_producto = #{idProducto} )

select ub.id_ubicacion as "id", 'N' as "check" from sstt_ubicaciones ub inner join sstt_comunas c on c.id_comuna = ub.id_comuna inner join sstt_provincias p on p.id_provincia = c.id_provincia inner join sstt_regiones r on r.id_region = p.id_region where ub.s_tipo = 'ST'
and ub.i_rut = #{rut,typeHandler=RunTypeHandler}
and upper(ub.s_nombre) like '%'||upper(#{nombre})||'%'
and ub.id_comuna = #{idComuna}
and p.id_region = #{idRegion}
and exists (select 1 from sstt_stecnicos_productos stp where stp.id_stecnico = ub.id_ubicacion and stp.id_producto = #{idProducto} )

update sstt_ordenes_trabajo ot 
set ot.id_stecnico = #{id}
where ot.id_ot = #{ot.id}

select d.i_destino ,u.s_nombre ,u.s_direccion ,c.s_nombre AS s_comuna ,u.s_telefono ,u.s_tipo from sstt_destinos d inner join sstt_ubicaciones u ON u.id_ubicacion = d.i_destino inner join sstt_stecnicos_productos stp ON stp.id_stecnico = u.id_ubicacion inner join sstt_comunas c ON c.id_comuna = u.id_comuna where d.s_vigente = 'S' and u.s_vigente = 'S' and stp.s_vigente = 'S' and d.id_origen = #{idUbicacion} and stp.id_producto = #{idProducto}
and stp.s_garantia = 'GP'
and stp.s_garantia = #{tipoOT}
order by s_tipo desc

select distinct d.i_destino, u.s_nombre, u.s_direccion, c.s_nombre as s_comuna, u.s_telefono, u.s_tipo, p.s_descripcion s_tipo_glosa from sstt_destinos d inner join sstt_ubicaciones u on u.id_ubicacion = d.i_destino inner join sstt_stecnicos_productos stp on stp.id_stecnico = u.id_ubicacion inner join sstt_comunas c on c.id_comuna = u.id_comuna inner join sstt_parametros p on p.s_nombre = u.s_tipo and p.s_string1 = 'UBIC' where d.s_vigente = 'S' and u.s_vigente = 'S' and stp.s_vigente = 'S'
and d.id_origen = #{idOrigen}
and stp.id_producto = #{idProducto}
and stp.s_garantia = 'GP'
and stp.s_garantia = #{tipoOT}
order by s_nombre asc

select stp.id_stecnico as i_destino, u.s_nombre, u.s_direccion, c.s_nombre as s_comuna, u.s_telefono, pa.s_descripcion as s_tipo_glosa from sstt_stecnicos_productos stp inner join sstt_ubicaciones u on stp.id_stecnico = u.id_ubicacion inner join sstt_comunas c on c.id_comuna = u.id_comuna inner join sstt_parametros pa on pa.s_nombre = stp.s_garantia and pa.s_string1 = 'TIOT' where stp.s_vigente = 'S' and u.s_vigente = 'S' and stp.id_producto = #{idProducto}
and exists (select 1 from sstt_destinos d where d.i_destino = stp.id_stecnico and d.id_origen = #{idOrigen} )
order by pa.s_descripcion desc, u.s_nombre asc
order by ${orderBy}
${sortOrder}

select stp.id_stecnico_producto, stp.id_stecnico, stp.id_producto, stp.s_garantia, stp.s_vigente from sstt_stecnicos_productos stp where stp.id_producto = #{idProducto} and stp.id_stecnico = #{idServicioTecnico}
and stp.s_garantia = 'GP'
and stp.s_garantia = #{tipoOT}

mapper.update:
update sstt_stecnicos_productos
set s_vigente = #{vigente}
where id_stecnico_producto = #{id}

update sstt_ubicaciones
set s_vigente = #{vigente}
where id_ubicacion = #{id}

		update sstt_ordenes_trabajo ot
		set ot.s_contrato_emitido = #{contratoEmitido}
			,ot.s_contrato      = #{numeroContrato}
			,ot.s_diagnostico   = #{diagnostico}

				,ot.s_califica_fr   = #{calificaFR}              

        where ot.id_ot = #{id}

mapper.select:
select distinct st.id_ubicacion ,st.s_nombre ,st.i_rut ,st.s_direccion ,st.s_telefono ,st.id_comuna ,c.s_nombre as s_comuna ,r.s_nombre as s_region from sstt_ubicaciones st inner join sstt_comunas c on c.id_comuna = st.id_comuna inner join sstt_provincias pr on pr.id_provincia = c.id_provincia inner join sstt_regiones r on r.id_region = pr.id_region left outer join sstt_stecnicos_productos stp on stp.id_stecnico = st.id_ubicacion left outer join sstt_destinos d on d.i_destino = st.id_ubicacion
st.s_tipo = 'ST' and st.s_vigente = 'S'
and stp.id_producto = #{idProducto}
and stp.s_garantia = 'GP'
and stp.s_garantia = #{tipoOT}
and d.id_origen = #{idOrigen}
and st.id_comuna = #{idComuna}
order by st.s_nombre asc

select distinct st.id_ubicacion || '_' ||m.id_marca as id ,e.id_usuario ,st.id_ubicacion ,m.id_marca ,trim(e.s_nombre) ||' '|| trim(e.s_apellido_paterno) as usuario_nombreCompleto ,st.s_nombre as ubicacion_nombre ,m.s_nombre as marca_nombre ,st.i_cantidad_productos ,st.i_rut as ubicacion_rut ,st.s_direccion as ubicacion_direccion ,c.s_nombre as nombre_comuna ,r.s_nombre as nombre_region ,r.i_orden from sstt_ubicaciones st inner join sstt_comunas c on c.id_comuna = st.id_comuna inner join sstt_provincias pr on pr.id_provincia = c.id_provincia inner join sstt_regiones r on r.id_region = pr.id_region inner join sstt_stecnicos_productos stp on stp.id_stecnico = st.id_ubicacion and stp.s_vigente = 'S' and stp.s_garantia = 'GP' inner join owv_productos p on p.id_producto = stp.id_producto inner join owv_marcas m on m.id_marca = p.id_marca left join sstt_ejecutivas_sta_marcas esm on esm.id_stecnico = st.id_ubicacion and esm.id_marca = p.id_marca left join sstt_usuarios e on e.id_usuario = esm.id_ejecutiva
st.s_vigente = 'S' and st.s_tipo = 'ST'
and lower(st.s_nombre) like lower('%' || #{nombreSTecnico} || '%')
and e.id_usuario = #{idEjecutiva}
and e.id_usuario is not null
and e.id_usuario is null
and p.id_marca = #{idMarca}
and r.id_region = #{idRegion}
and c.id_comuna = #{idComuna}
and p.id_familia = #{idFamilia}
and exists (select * from sstt_destinos d where d.id_origen = #{idSucursal} and d.i_destino = st.id_ubicacion and d.s_vigente = 'S')
and exists (select * from sstt_destinos d where d.id_origen = #{idBodega} and d.i_destino = st.id_ubicacion and d.s_vigente = 'S')
and p.id_proveedor = #{idProveedor}
and p.id_producto = #{idProducto}
order by ${orderBy}
${sortOrder}

select count(distinct st.id_ubicacion || '_' ||m.id_marca) from sstt_ubicaciones st inner join sstt_comunas c on c.id_comuna = st.id_comuna inner join sstt_provincias pr on pr.id_provincia = c.id_provincia inner join sstt_regiones r on r.id_region = pr.id_region inner join sstt_stecnicos_productos stp on stp.id_stecnico = st.id_ubicacion and stp.s_vigente = 'S' and stp.s_garantia = 'GP' inner join owv_productos p on p.id_producto = stp.id_producto inner join owv_marcas m on m.id_marca = p.id_marca left join sstt_ejecutivas_sta_marcas esm on esm.id_stecnico = st.id_ubicacion and esm.id_marca = p.id_marca left join sstt_usuarios e on e.id_usuario = esm.id_ejecutiva
st.s_vigente = 'S' and st.s_tipo = 'ST'
and lower(st.s_nombre) like lower('%' || #{nombreSTecnico} || '%')
and e.id_usuario = #{idEjecutiva}
and e.id_usuario is not null
and e.id_usuario is null
and p.id_marca = #{idMarca}
and r.id_region = #{idRegion}
and c.id_comuna = #{idComuna}
and p.id_familia = #{idFamilia}
and exists (select * from sstt_destinos d where d.id_origen = #{idSucursal} and d.i_destino = st.id_ubicacion and d.s_vigente = 'S')
and exists (select * from sstt_destinos d where d.id_origen = #{idBodega} and d.i_destino = st.id_ubicacion and d.s_vigente = 'S')
and p.id_proveedor = #{idProveedor}
and p.id_producto = #{idProducto}

select st.id_ubicacion ,st.s_direccion ,st.s_nombre ,co.s_nombre as s_nombre_comuna from sstt_ubicaciones st left join sstt_comunas co on co.id_comuna = st.id_comuna left join sstt_provincias pr on pr.id_provincia = co.id_provincia left join sstt_regiones re on re.id_region = pr.id_region
st.s_tipo = 'ST'
and co.id_comuna = #{idComuna}
and pr.id_provincia= #{idProvincia}
and re.id_region = #{idRegion}
and st.id_ubicacion not in
#{idStecnico}
and UPPER(st.s_nombre) like(UPPER(#{nombre}))
order by s_nombre_comuna asc
