mapper.select:
select t.id_tipo_falla ,t.id_producto ,t.id_familia ,t.s_descripcion ,t.s_vigente ,t.s_tipo ,(select count(*) from sstt_fallas_accesorios fa, sstt_accesorios a where fa.id_tipo_falla = t.id_tipo_falla and a.id_accesorio = fa.id_accesorio and a.s_vigente = 'S') as i_cantidad_accesorios from sstt_tipo_fallas t
and t.id_producto = #{idProducto} or t.id_familia = (select p.id_familia from owv_productos p where p.id_producto = #{idProducto})
and t.id_familia = #{idFamilia}
and a.id_accesorio = #{idAccesorio}
order by t.s_vigente DESC, t.s_descripcion ASC

select count(*)
from sstt_tipo_fallas_ot t
where t.id_ot = #{idOT}

mapper.delete:
delete
from sstt_tipo_fallas_ot t 
where t.id_ot = #{idOT}

mapper.select:
		select count(*)
        from sstt_tipo_fallas_ot t
        where t.id_ot = #{idOT}
        	and t.id_tipo_falla = #{idTipoFalla}

mapper.update:
update sstt_accesorios_ot ao
	set ao.s_requerido = 'S'
where ao.id_ot = #{idOT}
	and ao.id_accesorio = #{idAccesorio}

mapper.select:
select t.id_tipo_falla as id ,t.id_producto as "producto.id" ,t.id_familia as "producto.familia.id" ,t.s_descripcion as "descripcion" ,t.s_vigente as vigente ,t.s_tipo as tipo ,(select count(*) from sstt_fallas_accesorios fa, sstt_accesorios a where fa.id_tipo_falla = t.id_tipo_falla and a.id_accesorio = fa.id_accesorio and a.s_vigente = 'S') as cantidad ,f.s_nombre as "producto.familia.nombre" ,p.s_descripcion as "producto.descripcion" ,m.s_nombre as "producto.marca.nombre" from sstt_tipo_fallas t left join owv_familias f on f.id_familia = t.id_familia left join owv_productos p on p.id_producto = t.id_producto left join owv_marcas m on m.id_marca = p.id_marca
and t.id_producto = #{idProducto}
and t.id_familia = #{idFamilia}
and exists(select 1 from sstt_fallas_accesorios fa where fa.id_tipo_falla = t.id_tipo_falla and fa.id_accesorio = #{idAccesorio} )
order by ${orderBy}
${sortOrder}

select t.id_tipo_falla as id ,t.id_producto as "producto.id" ,t.id_familia as "producto.familia.id" ,t.s_descripcion as "descripcion" ,t.s_vigente as vigente ,t.s_tipo as tipo ,(select count(*) from sstt_fallas_accesorios fa, sstt_accesorios a where fa.id_tipo_falla = t.id_tipo_falla and a.id_accesorio = fa.id_accesorio and a.s_vigente = 'S') as cantidad ,f.s_nombre as "producto.familia.nombre" ,p.s_descripcion as "producto.descripcion" ,m.s_nombre as "producto.marca.nombre" from sstt_tipo_fallas t left join owv_familias f on f.id_familia = t.id_familia left join owv_productos p on p.id_producto = t.id_producto left join owv_marcas m on m.id_marca = p.id_marca
and t.id_producto = #{idProducto}
and t.id_familia = #{idFamilia}
and not exists(select 1 from sstt_fallas_accesorios fa where fa.id_tipo_falla = t.id_tipo_falla and fa.id_accesorio = #{idAccesorio} )
order by ${orderBy}
${sortOrder}

		select 
			count(t.id_tipo_falla)
		from 
			sstt_tipo_fallas t
			left join owv_familias f on f.id_familia = t.id_familia
			left join owv_productos p on p.id_producto = t.id_producto
	    	left join owv_marcas m on m.id_marca = p.id_marca	


			    and t.id_producto = #{idProducto}


			    and t.id_familia = #{idFamilia}


			    and exists(select 1 from sstt_fallas_accesorios fa where fa.id_tipo_falla = t.id_tipo_falla and fa.id_accesorio = #{idAccesorio} )

                    and t.id_producto = #{idProducto}


                    and t.id_familia = #{idFamilia}


                    and not exists(select 1 from sstt_fallas_accesorios fa where fa.id_tipo_falla = t.id_tipo_falla and fa.id_accesorio = #{idAccesorio} )




			order by ${orderBy}

				${sortOrder}

		select 
			count(*)
        from 
        	sstt_tipo_fallas t

	  		t.s_descripcion = #{descripcion}

	            and t.id_producto = #{producto.id}


	            and t.id_familia = #{familia.id}

mapper.update:
update 
	sstt_tipo_fallas 
set 
	s_vigente = #{vigente} 
where 
	id_tipo_falla = #{id}
