mapper.select:
select ubi.id_ubicacion_interna ,ubi.s_codigo ,ubi.s_tipo ,pa.s_descripcion as s_nombre_tipo ,ubi.s_nombre ,ubi.s_vigente ,ubi.d_fecha_creacion ,ubi.id_usuario ,trim(us.s_nombre)||' '||trim(us.s_apellido_paterno)||' '||trim(us.s_apellido_materno) as s_usuario_creacion ,ubi.d_fecha_modificacion ,ubi.id_usuario_modificacion ,trim(us2.s_nombre)||' '||trim(us2.s_apellido_paterno)||' '||trim(us2.s_apellido_materno) as s_usuario_modificacion ,count(ubid.id_sucursal) as i_sucursales ,count(ubid.id_familia) as i_familias ,count(ubid.id_linea) as i_lineas from sstt_ubicaciones_internas ubi left join sstt_parametros pa on pa.s_nombre = ubi.s_tipo left join sstt_usuarios us on us.id_usuario = ubi.id_usuario left join sstt_usuarios us2 on us2.id_usuario = ubi.id_usuario_modificacion left join sstt_ubicacion_interna_det ubid on ubid.id_ubicacion_interna = ubi.id_ubicacion_interna left join sstt_ubicacion_interna_det ubid2 on ubid2.id_ubicacion_interna = ubi.id_ubicacion_interna left join sstt_ubicacion_interna_det ubid3 on ubid3.id_ubicacion_interna = ubi.id_ubicacion_interna left join sstt_ubicacion_interna_det ubid4 on ubid4.id_ubicacion_interna = ubi.id_ubicacion_interna
ubi.s_tipo in ('BG','JL','GO') and ubi.id_ubicacion_padre = 10015 and ubi.id_ubicacion_interna not in(1001500001,1001500002,1001500003)
and UPPER(ubi.s_nombre) = UPPER(#{nombre})
and ubid.id_linea = #{idLinea}
and ubid2.id_familia =#{idFamilia}
and ubid3.id_sucursal = #{idSucursal}
and ubid4.id_producto = #{idProducto}
and UPPER(ubi.s_codigo) = UPPER(#{codigo})
and ubi.s_vigente = #{vigente}
and ubi.id_ubicacion_interna!=#{notId}
group by ubi.id_ubicacion_interna ,ubi.s_codigo ,pa.s_descripcion ,ubi.s_tipo ,ubi.s_nombre ,ubi.s_vigente ,ubi.d_fecha_creacion ,ubi.id_usuario ,ubi.d_fecha_modificacion ,ubi.id_usuario_modificacion ,trim(us.s_nombre)||' '||trim(us.s_apellido_paterno)||' '||trim(us.s_apellido_materno) ,trim(us2.s_nombre)||' '||trim(us2.s_apellido_paterno)||' '||trim(us2.s_apellido_materno)
order by ${orderBy}
${sortOrder}

select 
    count (distinct ubi.id_ubicacion_interna)
from sstt_ubicaciones_internas ubi
    left join sstt_ubicacion_interna_det ubid on ubid.id_ubicacion_interna = ubi.id_ubicacion_interna
    left join sstt_ubicacion_interna_det ubid2 on ubid2.id_ubicacion_interna = ubi.id_ubicacion_interna
    left join sstt_ubicacion_interna_det ubid3 on ubid3.id_ubicacion_interna = ubi.id_ubicacion_interna
    left join sstt_ubicacion_interna_det ubid4 on ubid4.id_ubicacion_interna = ubi.id_ubicacion_interna

    ubi.s_tipo in ('BG','JL','GO')
    and ubi.id_ubicacion_padre = 10015
    and ubi.id_ubicacion_interna not in(1001500001,1001500002,1001500003)

        and UPPER(ubi.s_nombre) = UPPER(#{nombre})


        and ubid.id_linea = #{idLinea}


        and ubid2.id_familia =#{idFamilia}


        and ubid3.id_sucursal = #{idSucursal}


        and ubid4.id_producto = #{idProducto}


        and UPPER(ubi.s_codigo) = UPPER(#{codigo})


    	and ubi.s_vigente = #{vigente}


    	and ubi.id_ubicacion_interna!=#{notId}

mapper.insert:

    select ssts_ubicaciones_internas.nextval from dual

insert into sstt_ubicaciones_internas(
    id_ubicacion_interna
    ,s_codigo
    ,s_tipo
    ,s_nombre
    ,id_usuario
    ,s_vigente
    ,d_fecha_creacion
    ,s_descripcion
    ,id_ubicacion_padre
    ,s_inventario
)
values (
    #{id}
    ,UPPER(#{codigo})
    ,#{tipo.codigo}
    ,UPPER(#{nombre})
    ,#{usuario.id}
    ,#{vigente}
    ,sysdate
    ,#{descripcion}
    ,#{ubicacionPadre.id}
    ,'N'
)

mapper.update:
update sstt_ubicaciones_internas
set s_tipo = #{tipo.codigo}
    ,s_nombre = #{nombre}
    ,s_vigente = #{vigente}
    ,id_usuario_modificacion=#{usuarioModificacion.id}
    ,d_fecha_modificacion = sysdate
where s_codigo = #{codigo}

mapper.select:
select ubi.id_ubicacion_interna ,ubi.s_nombre ,count(bi.id_ubicacion_interna) as i_total_productos from sstt_ubicaciones_internas ubi left join sstt_bitacoras_internas bi on bi.id_ubicacion_interna = ubi.id_ubicacion_interna and bi.d_fecha_termino is null where ubi.id_ubicacion_padre = 10015 and ubi.s_inventario = 'N' group by ubi.id_ubicacion_interna ,ubi.s_nombre ,bi.id_ubicacion_interna
order by ${orderBy}
${sortOrder}

mapper.update:
update sstt_ubicaciones_internas
	set s_inventario = #{inventario}
where id_ubicacion_interna = #{id}

update sstt_ubicaciones_internas
	set s_inventario = #{inventario}
where id_ubicacion_interna = #{id}
