mapper.select:
select u.id_usuario, u.s_nombre, u.s_apellido_paterno, u.s_apellido_materno, (u.s_nombre || ' ' || u.s_apellido_paterno || ' ' || u.s_apellido_materno) as nombre_Completo, u.s_vigente, count(1) i_ubicaciones from sstt_usuarios u inner join sstt_usuarios_ubicaciones uub on u.id_usuario = uub.id_usuario
and u.id_usuario = #{run,typeHandler=RunTypeHandler}
and exists (select 1 from sstt_ubicaciones ub where ub.id_ubicacion = uub.id_ubicacion and ub.id_ubicacion = #{idUbicacion})
group by u.id_usuario, u.s_nombre, u.s_apellido_paterno, u.s_apellido_materno, u.s_vigente
order by u.s_vigente desc, u.s_nombre asc
order by ${orderBy}
${sortOrder}

select count(distinct u.id_usuario) from sstt_usuarios u inner join sstt_usuarios_ubicaciones uub on u.id_usuario = uub.id_usuario
and u.id_usuario = #{run,typeHandler=RunTypeHandler}
and exists (select 1 from sstt_ubicaciones ub where ub.id_ubicacion = uub.id_ubicacion and ub.id_ubicacion = #{idUbicacion})

mapper.update:
update sstt_usuarios 
set s_vigente = #{vigente} 
where id_usuario = #{id}

update sstt_usuarios set
	s_nombre           = #{nombre}
	,s_login            = #{id}
	,s_apellido_paterno = #{apellidoPaterno}
	,s_apellido_materno = #{apellidoMaterno}
	,s_celular          = #{celular}
	,s_interno          = #{anexo}
	,s_email            = #{email}
where
	id_usuario = #{id}
